tinymce.PluginManager.add('divRightServicios', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('customDivRightElementButton', {
        text: 'Div Right',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Please input text',
                body: [
                    {type: 'textbox', name: 'description', label: 'Text'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('<div class="service-right">' + e.data.description + '</div>');
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('customEmElementMenuItem', {
        text: 'Div Right Element',
        context: 'tools',
        onclick: function() {
            editor.insertContent('<div class="service-right">&nbsp;</div>');
        }
    });
});